import { View, Text, Image, ScrollView } from 'react-native';
import React from 'react';
import { postData } from './postData';
import { TouchableOpacity } from 'react-native-gesture-handler';

const HomePosts = ({navigation}) => {
  return (
    <ScrollView style={{marginTop:15}} >
      {postData.map(
          (post, index) =>
          <View key ={index}>
             <TouchableOpacity onPress={() => navigation.push('RestaurantPage')}>
                  <View 
            style={{marginBottom:20, justifyContent:'space-around', padding:10}}  >
                    <Image style={{width:370, height:220}}  source={{uri: post.image}} />
                    <View style = {{flexDirection:'row', justifyContent:'space-between', alignItems:'center', marginTop:10}} >
                       <View>
                       <Text style ={{fontSize: 15, fontWeight:'bold'}}>{post.name}</Text>
                       <Text style ={{fontSize:13, color: "grey"}}>{post.location}</Text>
                       </View>
                        <View style = {{justifyContent:'center', alignItems:'center',borderRadius:15, backgroundColor: "green", height: 30, width: 30}}>
                            <Text style={{color:'white'}} >{post.ratings}</Text>
                    </View>
                    </View>
            </View>
            </TouchableOpacity>
          </View>)}
    </ScrollView>
  )
  }


export default HomePosts;
